import Vue from 'vue'
import Vuex from 'vuex'
import axios from 'axios'
Vue.use(Vuex)
const actions = {
    SendTodo(context,value){
        axios.post('http://localhost:8081/addtodo',value).then(
            response =>{
                console.log('請求成功了，這是資料庫回傳的data',response.data)
                localStorage.setItem('userId', response.data.userid);
                context.dispatch('gettodo')
            },
            error => {
                console.log('請求失敗了',error.message)
            }
        )
        
    },
    gettodo(context){
        axios.get('http://localhost:8081/todo').then(
            response =>{
                console.log('請求成功了',response.data)
                context.commit('GETTODO',response.data)
            },
            error => {
                console.log('請求失敗了',error.message)
            }
        )
    },
    deletetodo(context,id){
        axios.post('http://localhost:8081/deletetodo',{'id': id}).then(
            response =>{
                console.log('請求成功了',response.data)
                context.dispatch('gettodo')
            },
            error => {
                console.log('請求失敗了',error.message)
            }
        )
    },
    getusertodo(context,userid){
        console.log(userid)
    }
}

const mutations = {
    SENDTODO(state,value){
        state.todo.push(value)
    },
    GETTODO(state,value){
        state.todo=value
    }
}

const state = {
    todo:[]
}


const store = new Vuex.Store({
    actions,
    mutations,
    state
})

export default store
