import Vue from 'vue'
import Vuex from 'vuex'
import axios from 'axios'
Vue.use(Vuex)
const actions = {
    SendTodo(context,value){
        console.log(context,value)
        // fetch('http://localhost:8081/todo', {
        //     method: 'GET',
        // }).then(response => response.json())
        // .then(data => console.log(data,123))
        // .catch((error) => {
        //   console.error('Error:', error);
        // });
        
    },
    gettodo(context){
        axios.get('http://localhost:8081/todo').then(
            response =>{
                console.log('請求成功了',response.data)
                context.commit('GETTODO',response.data)
            },
            error => {
                console.log('請求失敗了',error.message)
            }
        )
    },
    deletetodo(context,id){
        axios.post('http://localhost:8081/deletetodo',JSON.stringify(id)).then(
            response =>{
                console.log('請求成功了',response.data)
                
            },
            error => {
                console.log('請求失敗了',error.message)
            }
        )
    }
}

const mutations = {
    SENDTODO(state,value){
        state.todo.push(value)
    },
    GETTODO(state,value){
        state.todo=value
    }
}

const state = {
    todo:[]
}


const store = new Vuex.Store({
    actions,
    mutations,
    state
})

export default store
